const { exec } = require('child_process');

const scriptPath = '/Users/chaitanyajamwal/Downloads/YourPlumberAI/YourPlumberAI sever/YourPlumberAI_Outreach/execution/check_replies.py';

return new Promise((resolve, reject) => {
  exec(`python3 "${scriptPath}"`, (error, stdout, stderr) => {
    if (error) {
      // Even if there is an error, we resolve to show what happened
      resolve([
        {
          json: {
            success: false,
            error: error.message,
            stdout: stdout,
            stderr: stderr
          }
        }
      ]);
      return;
    }
    resolve([
      {
        json: {
          success: true,
          stdout: stdout,
          stderr: stderr
        }
      }
    ]);
  });
});
